export default function appReducer(state = {}, action) {
  switch (action.type) {
    case 'SET_CATEGORY':
      const productsByCategory = action.payload.reduce(
        (accumulator, product) => {
          const category = product.category;
          return {
            ...accumulator,
            [category]: [...(accumulator[category]) || [], product],
          };
        },
        {}
      );
      return { ...state, productsByCategory }

    default:
      return state;
  }
}
