const SET_CATEGORY = 'SET_CATEGORY';

export const setCategory = (selectedCategory) => ({
  type: SET_CATEGORY,
  payload: selectedCategory
});

export const setProduct = (selectedProduct) => ({
  type: SET_PRODUCT,
  payload: selectedProduct
});
