import React, { useState } from 'react';
// import { CategoriesList.react } from './CategoriesList.react.js';
import { useDispatch, useSelector } from 'react-redux';
// import thunk from 'redux-thunk';
import { addProduct } from '../actions/productsActions';

function App() {
  const [newProduct, setNewProduct] = useState('')

  const products = useSelector((state) => (state.products || {}).value);
  const dispatch = useDispatch();

  return (
    <div>
      <h1>HELLO WORLD</h1>
      <input type="text" value={newProduct} onChange={(e) => setNewProduct(e.target.value)} />
      <button onClick={() => dispatch(addProduct(newProduct))}>
        Add product to store1
      </button>

      <ul>
        {products.map(product => <li key={product}>{product}</li>)}
      </ul>

    </div>
  );
}

export default App;

