import React from 'react'
import { useSelector } from 'react-redux';
import { CardMedia, Typography, makeStyles, InputLabel, MenuItem, FormControl, Select, Button } from "@material-ui/core";
import { grey } from '@material-ui/core/colors';

const useStyles = makeStyles({
  image: {
    width: 300,
    height: 300,
  },
  formControl: {
    margin: 10,
    minWidth: 100,
  },
  container: {
    padding: 20,
  },
  sizeSelect: {
    height: 50,
    width: 80,
    padding: 10,
  },
  titleCase: {
    textTransform: 'capitalize',
  },
  description: {
    color: grey,
    padding: 10,
  }
});

export default function ProductDetails() {
  const classes = useStyles();
  const { selectedProductID } = useSelector(state => state.app)
  const selectedProduct = useSelector(state => state.products.productsByID[selectedProductID]);

  console.log(selectedProduct.description);

  return (
    <div className={classes.container}>
      <CardMedia
        className={classes.image}
        imageURL={selectedProduct.image}
        title="product image"
      />
      <div className={classes.productTitle}>
        <Typography variant='h6' className={classes.titleCase}>
          {selectedProduct.title}
        </Typography>
      </div>
      <div className={classes.price}>
        <Typography variant='h8' className={classes.titleCase}>
          ${selectedProduct.price}
        </Typography>
      </div>
      <FormControl variant="filled" className={classes.formControl}>
        <InputLabel id="select-size">Size</InputLabel>
        <Select
          labelId="select-size"
          id="select-size"
        >
          <MenuItem>XS</MenuItem>
          <MenuItem>S</MenuItem>
          <MenuItem>M</MenuItem>
          <MenuItem>L</MenuItem>
          <MenuItem>XL</MenuItem>
        </Select>
      </FormControl>
      <FormControl variant="filled" className={classes.formControl}>
        <InputLabel id="select-quantity">Quantity</InputLabel>
        <Select
          labelId="select-quantity"
          id="select-quantity"
        >
          <MenuItem>1</MenuItem>
          <MenuItem>2</MenuItem>
          <MenuItem>3</MenuItem>
          <MenuItem>4</MenuItem>
          <MenuItem>5</MenuItem>
        </Select>
      </FormControl>
      <div className={classes.description}>
        <Typography variant='h8'>
          {selectedProduct.description}
        </Typography>
      </div>
      <Button variant="contained" color="primary">
        Add to Cart
      </Button>
    </div >
  )
}
