import React from 'react';
import { useSelector } from 'react-redux';
import { makeStyles } from '@material-ui/core';

const useStyles = makeStyles({
  container: {
    display: 'flex',
    justifyContent: 'center',
  },
  image: {
    width: 500,
    height: 500,
    margin: 10,
  },
  formControl: {
    margin: 10,
    minWidth: 100,
  },
  productInfo: {
    margin: 10,
    maxWidth: 500
    // display: flex,
  },
  sizeSelect: {
    height: 50,
    width: 80,
    padding: 10,
  },
  titleCase: {
    textTransform: 'capitalize',
  },
  description: {
    color: grey,
    padding: 10,
  }
});

export default function SearchResultOverlay() {
  const classes = useStyles();
  // const { selectedProductID, selectedCategory } = useSelector(state => state.app)
  const { searchValue } = useSelector(state => state.app);

  // const productsByID = useSelector(state => state.products.productsByID)
  // const selectedProduct = productsByID[selectedProductID];

  // const renderSelectSize = () => {
  //   if (!["men clothing", "women clothing"].includes(selectedCategory)) { return null }

  return (
    <div>
      <h1>Search Results for "{searchValue}"</h1>
    </div>
  )
}
