export const SET_CATEGORY = 'SET_CATEGORY';
export const SET_PRODUCT = 'SET_PRODUCT';

export const setCategory = () => ({
  type: SET_CATEGORY,
  payload: selectedCategory
});

export const setProduct = (selectedProduct) => ({
  type: SET_PRODUCT,
  payload: selectedProductID
});
