  //     case SET_PRODUCT:
  //       return { ...state, selectedProductID: action.payload, activePage: 'product' }
  //     default:
  //       return state;
  //   }
  // }

  //   const renderContents = () => {
  //     switch(act) {

  //     }
  //   }


  return (
    <div className={classes.app}>
      {activePageSwitch}
      {/* {renderContents()} */}
      {/* {selectedProduct != null ? (
        <ProductDetails />
      ) : (
          <Category />
        ) : (
      <CategoriesList />
        )} */}
    </div>
  );
}

export default App;
